// --- filter_top_sentiment_score in n8n ---
// Convert sentimentalScore to number, filter & sort descending

// 1ï¸âƒ£ Define how many top results you want
const topN = 5;

// 2ï¸âƒ£ Clean, convert, and sort
const cleaned = items
  .map(item => {
    const score = Number(item.json.sentimentalScore);
    return { ...item, json: { ...item.json, sentimentalScore: score } };
  })
  .filter(item => !isNaN(item.json.sentimentalScore)) // remove blanks or invalids
  .sort((a, b) => b.json.sentimentalScore - a.json.sentimentalScore)
  .slice(0, topN);

// 3ï¸âƒ£ Return top-scoring items
return cleaned;

// --- SAFE ACCESS HELPERS ---
function safeGet(nodeName) {
  try {
    const n = $node[nodeName];
    if (!n) return [];
    if (typeof n.all === "function") return n.all();
    if (Array.isArray(n.json)) return n.json;
    if (n.json) return [n.json];
    return [];
  } catch (e) {
    console.log(`âš ï¸ Node "${nodeName}" not executed or empty`);
    return [];
  }
}
// -----------------------------------------------------------

// 1ï¸âƒ£ Gather data safely
const accountNode = safeGet("Alpaca-get-account-info")[0]?.json || {};
const openPositionsRaw = safeGet("Alpaca_get_open_positions");
const inputItems = $input.all();

// 2ï¸âƒ£ Normalize open positions from Alpaca
const openPositions = [];
for (const pos of openPositionsRaw) {
  if (Array.isArray(pos.json?.body)) openPositions.push(...pos.json.body);
  else if (pos.json?.symbol) openPositions.push(pos.json);
}
const openSymbols = openPositions.map(p => p.symbol);

// 3ï¸âƒ£ Account info fallback
const availableCash = parseFloat(accountNode.cash ?? "0");
const equity = parseFloat(accountNode.equity ?? availableCash);

// 4ï¸âƒ£ Parameters
const MAX_ALLOCATION_PERCENT = 0.10;  // 10% per stock max
const MIN_SCORE_THRESHOLD = 0.5;      // min abs(score) to trade

const output = [];

console.log("ðŸ§¾ Incoming sentiment items:", inputItems.length);
console.log("ðŸ’° Account equity:", equity, "| Cash:", availableCash);
console.log("ðŸ“¦ Current holdings:", openSymbols);

// 5ï¸âƒ£ Core logic
for (const item of inputItems) {
  const symbol = item.json.stock || item.json.symbol;
  const rawScore = (
    item.json.sentimentalScore ??
    item.json.sentimentScore ??
    "0"
  ).toString().trim();
  const score = parseFloat(rawScore);

  if (!symbol || isNaN(score)) {
    console.log(`âš ï¸ Skipping invalid item:`, item.json);
    continue;
  }

  const conviction = Math.max(0, (Math.abs(score) - MIN_SCORE_THRESHOLD) / (1.0 - MIN_SCORE_THRESHOLD));
  const allocationPercent = MAX_ALLOCATION_PERCENT * conviction;
  const notional = equity * allocationPercent;

  let side = "hold"; // default action
  let reason = "";

  if (score >= MIN_SCORE_THRESHOLD && !openSymbols.includes(symbol)) {
    side = "buy";
    reason = "Positive sentiment, not owned";
  } else if (score <= -MIN_SCORE_THRESHOLD && openSymbols.includes(symbol)) {
    side = "sell";
    reason = "Negative sentiment, currently owned";
  } else {
    reason = "Hold condition (either weak sentiment or already aligned)";
  }

  item.json.symbol = symbol;
  item.json.sentimentScore = score;
  item.json.side = side;
  item.json.reason = reason;
  item.json.notional = parseFloat(notional.toFixed(2));
  item.json.allocationPercent = allocationPercent;

  output.push(item);
}

// 6ï¸âƒ£ Log summary
const buys = output.filter(i => i.json.side === "buy").map(i => i.json.symbol);
const sells = output.filter(i => i.json.side === "sell").map(i => i.json.symbol);
const holds = output.filter(i => i.json.side === "hold").map(i => i.json.symbol);

console.log(`ðŸ“ˆ BUY: ${buys.join(", ") || "None"}`);
console.log(`ðŸ“‰ SELL: ${sells.join(", ") || "None"}`);
console.log(`ðŸ¤ HOLD: ${holds.join(", ") || "None"}`);

// 7ï¸âƒ£ Always return output (even if empty)
if (output.length === 0) {
  console.log("âš ï¸ No valid sentiment items received. Returning empty placeholder.");
  return [{ json: { message: "No trades today", side: "none" } }];
}

return output;

// --- Normalize Alpaca positions response safely ---

// Get full input item
const item = $input.first();

// Handle missing input gracefully
if (!item || !item.json) {
  return [{ json: { noPositions: true } }];
}

const input = item.json;

// If Alpaca returned full HTTP response with 'body'
if (Array.isArray(input.body)) {
  if (input.body.length === 0) {
    // No open positions
    return [{ json: { noPositions: true } }];
  }
  // Return each position as its own item
  return input.body.map(pos => ({ json: pos }));
}

// If Alpaca returned array directly (not wrapped)
if (Array.isArray(input)) {
  if (input.length === 0) {
    return [{ json: { noPositions: true } }];
  }
  return input.map(pos => ({ json: pos }));
}

// If there's a single position object
return [{ json: input }];

// --- Create Positions to Open AND Close ---

// 1ï¸âƒ£ Get sentiment and Alpaca data safely
const topSentimentRaw = $("filter_top_sentiment_score").all() || [];
let openPositions = $("Normalize empty positions").all() || [];
const accountInfo = $("Alpaca-get-account-info").first()?.json || {};

// âœ… Helper
const getSymbol = (obj) => obj.json.symbol || obj.json.stock || "";

// Deduplicate top sentiments
const topSentimentMap = new Map();
topSentimentRaw.forEach(item => {
  const symbol = getSymbol(item);
  if (symbol && !topSentimentMap.has(symbol)) {
    const score = parseFloat(item.json.sentimentalScore || item.json.sentimentScore || "0");
    topSentimentMap.set(symbol, { symbol, sentimentScore: score });
  }
});
const topSentiment = Array.from(topSentimentMap.values());

// 2ï¸âƒ£ Extract lists
const openSymbols = openPositions.map(p => p.json.symbol);
const availableCash = parseFloat(accountInfo.cash || "0");

// --- POSITIONS TO CLOSE ---
const positions_to_close = openPositions
  .filter(p => {
    const symbol = p.json.symbol;
    const s = topSentiment.find(x => x.symbol === symbol);
    return !s || s.sentimentScore <= 0;
  })
  .map(p => ({
    symbol: p.json.symbol,
    qty: parseFloat(p.json.qty || "0"),
    market_value: parseFloat(p.json.market_value || "0"),
    action: "close",
    reason: "Negative or missing sentiment â†’ close position"
  }));

// --- POSITIONS TO OPEN ---
const positive = topSentiment.filter(s => s.sentimentScore > 0);
let newSymbols = positive.filter(s => !openSymbols.includes(s.symbol));

if (openPositions.length === 0 && newSymbols.length === 0 && positive.length > 0)
  newSymbols = positive.slice(0, 1);

const allocationFraction = 0.1;
const totalAllocation = availableCash * allocationFraction;
const valuePerSymbol = newSymbols.length > 0 ? totalAllocation / newSymbols.length : 0;

const positions_to_open = newSymbols.map(s => ({
  symbol: s.symbol,
  sentimentScore: s.sentimentScore,
  market_value_per_symbol: parseFloat(valuePerSymbol.toFixed(2)),
  action: "open",
  reason: "Positive sentiment â†’ buy position"
}));

// âœ… Log results
console.log("âœ… Positions to Open:", positions_to_open);
console.log("âŒ Positions to Close:", positions_to_close);

// âœ… Return both in one object for SplitOut nodes
return [{
  json: {
    positions_to_open,
    positions_to_close
  }
}];

// --- Split positions_to_open array into individual items ---
const data = $input.first().json;

if (!data.positions_to_open || !Array.isArray(data.positions_to_open) || data.positions_to_open.length === 0) {
  return [{ json: { message: "No positions to open" } }];
}

// Split into multiple items
return data.positions_to_open.map(item => ({ json: item }));

const data = $input.first().json;
if (!data.positions_to_close || !Array.isArray(data.positions_to_close) || data.positions_to_close.length === 0) {
  return [{ json: { message: "No positions to close" } }];
}
return data.positions_to_close.map(item => ({ json: item }));

// --- Combine Buy + Sell Order Results ---

let buyOrders = [];
let sellOrders = [];

// ðŸ›¡ï¸ Safe attempt to read data from previous nodes
try {
  buyOrders = $("Alpaca-post-order-buy").all() || [];
} catch (e) {
  buyOrders = [];
}

try {
  sellOrders = $("Alpaca-post-order-sell").all() || [];
} catch (e) {
  sellOrders = [];
}

// ðŸ§© Merge both safely
const merged = [...buyOrders, ...sellOrders]
  .map(item => {
    const o = item.json || {};
    return {
      symbol: o.symbol || null,
      side: o.side || (o.notional ? "buy" : "sell"),
      qty: o.qty ? Number(o.qty) : null,
      notional: o.notional ? Number(o.notional) : null,
      status: o.status || "unknown",
      created_at: o.created_at || new Date().toISOString(),
      id: o.id || null,
      reason: o.reason || "",
    };
  });

// âœ… If no orders were placed
if (merged.length === 0) {
  return [{ json: { message: "No trades executed today" } }];
}

// âœ… Return final merged orders ready for Google Sheets
return merged.map(m => ({ json: m }));

