from datetime import datetime

# Try to convert items safely into a list of dicts
try:
    input_items = [dict(i["json"]) for i in _items]
except Exception:
    input_items = []

# Handle empty data
if not input_items:
    output = [{"json": {"message": "No trade data received"}}]

# Handle simple 'no trades' message
elif len(input_items) == 1 and "message" in input_items[0]:
    output = [{"json": {"message": input_items[0]["message"]}}]

else:
    buy_count = 0
    sell_count = 0
    lines = []

    for data in input_items:
        symbol = data.get("symbol", "UNKNOWN")
        side = data.get("side", "unknown").lower()
        qty = data.get("qty") or data.get("notional") or "N/A"
        status = data.get("status", "unknown")
        created_at = data.get("created_at", "")

        if created_at:
            try:
                created_at = datetime.fromisoformat(
                    created_at.replace("Z", "+00:00")
                ).strftime("%Y-%m-%d %H:%M:%S")
            except Exception:
                pass

        if side == "buy":
            buy_count += 1
        elif side == "sell":
            sell_count += 1

        lines.append(
            f"â€¢ {symbol} â€” {side.upper()} â€” Qty: {qty} â€” {status}"
            + (f" â€” {created_at}" if created_at else "")
        )

    header = f"ðŸ“Š Trade Summary: {buy_count} Buy(s), {sell_count} Sell(s)"
    body = "\n".join(lines) if lines else "No detailed trades available."
    message = f"{header}\n\n{body}"

    output = [{"json": {"message": message}}]

return output
